// 1. 如果then方法中（成功或者失败）如果返回的不是一个promise。会将这个结果值传递给外层下一次的then的成功结果中
// 2. 如果执行then方法中的方法出错啦，抛出异常 走到下一个的失败中
// 3. 如果返回的是promise的结果会作为下一次then的成功或者失败。

//什么情况会失败
// 1. 出错会失败
// 2. 返回的promise出错会失败

//错误处理，处理是找最近的错误处理方法
//catch 就是then的别名 没有成功只要失败回调函数 （找最近的错误处理函数优先处理，处理不了找下一个最近的错误处理函数）

const STATUS = {
    PADDING: 'PADDING',
    FUFILLED: 'FUFILLED',
    REJECTED: 'REJECTED',
};

// 1. 如果x是promise，则要采用他的状态
// 2. 如果x是普通值，则直接resolve(x)
function resolvePromise (x, promise2, resolve, reject) {
    //如果是相同的则抛出一个类型错误
    if (promise2 == x) {
        reject(new TypeError('something wrong'));
    }
    if (typeof x === 'object' && x != null || typeof x === 'function') {
        let called;
        try {
            let then = x.then;
            if (typeof then === 'function') {
                then.call(x, function (y) {
                    if (called) return;
                    called = true;
                    resolvePromise(y, promise2, resolve, reject);
                }, function (r) {
                    if (called) return;
                    called = true;
                    reject(r);
                });
            } else {
                resolve(x);
            }
        } catch (error) {
            if (called) return;
            called = true;
            reject(error);
        }

    } else {
        resolve(x);
    }
}

//判断是否是promise
function isPromise (val) {
    return typeof val.then == 'function';
}

// promise 的executor 默认执行
class Promise {
    constructor(executor) {
        this.status = STATUS.PADDING;
        this.value = undefined;
        this.reason = undefined;
        this.onResolvedCallbacks = [];
        this.onRejectCallbacks = [];
        //当在executor中的同步或者异步方法执行结果触发时。会调用缓存列表中的事件。 发布
        const resolve = (val) => {
            //如果是promise 就继续递归解析
            if (val instanceof Promise) {
                return val.then(resolve, reject);
            }
            if (this.status === STATUS.PADDING) {
                this.status = STATUS.FUFILLED;
                this.value = val;
                this.onResolvedCallbacks.forEach(fn => {
                    fn();
                });
            }
        };
        const reject = (reason) => {
            if (this.status === STATUS.PADDING) {
                this.status = STATUS.REJECTED;
