                    } catch (error) {
                        reject(error);
                    }
                }, 0);
            }
            if (this.status === STATUS.REJECTED) {
                setTimeout(() => {
                    try {
                        let x = onRejected(this.reason);
                        resolvePromise(x, promise2, resolve, reject);
                    } catch (error) {
                        reject(error);
                    }
                }, 0);

            }
            if (this.status === STATUS.PADDING) {
                this.onResolvedCallbacks.push(() => {
                    setTimeout(() => {
                        try {
